#!/bin/bash

# Step 1: Set up identities
dfx identity new minter
dfx identity use minter
export MINTER=$(dfx identity get-principal)

export TOKEN_NAME="TEFRO"  # Token name
export TOKEN_SYMBOL="TFR"  # Token symbol


dfx identity use default
export DEFAULT=$(dfx identity get-principal)

export PRE_MINTED_TOKENS=1_000_000_000  # Total pre-minted token amount
export TRANSFER_FEE=100  # Transfer fee

dfx identity new archive_controller
dfx identity use archive_controller
export ARCHIVE_CONTROLLER=$(dfx identity get-principal)
export TRIGGER_THRESHOLD=2000
export NUM_OF_BLOCK_TO_ARCHIVE=1000
export CYCLE_FOR_ARCHIVE_CREATION=1000000000000

export FEATURE_FLAGS=true  # Enable feature flags

# Step 2: Deploy the canister with automated arguments
dfx deploy icrc1_ledger_canister   --mode reinstall --argument "(variant { Init = record {
     token_symbol = \"${TOKEN_SYMBOL}\";
     token_name = \"${TOKEN_NAME}\";
     minting_account = record { owner = principal \"${MINTER}\" };
     transfer_fee = ${TRANSFER_FEE};
     metadata = vec {
 record {
    \"icrc1:name\";
    variant {
        Text=\"Tefro\"
    };
     \"icrc1:logo\";
      variant {
        Text = \"data:image/png;base64,\"
      };
    };
};
     feature_flags = opt record { icrc2 = ${FEATURE_FLAGS} };
     initial_balances = vec { record { record { owner = principal \"${DEFAULT}\"; }; ${PRE_MINTED_TOKENS}; }; };
     archive_options = record {
         num_blocks_to_archive = ${NUM_OF_BLOCK_TO_ARCHIVE};
         trigger_threshold = ${TRIGGER_THRESHOLD};
         controller_id = principal \"${ARCHIVE_CONTROLLER}\";
         cycles_for_archive_creation = opt ${CYCLE_FOR_ARCHIVE_CREATION};
     };
 }})"

# Step 3: Verify deployment (optional)
# You can add verification commands here if necessary

echo "Deployment to mainnet complete."
